    private void initLogicComponents() {
        try {
            if(!SQLHelper.conectToLocalDatabase()){
                System.exit(0);
            }
        } catch (ClassNotFoundException | SQLException ex) {
            Logger.getLogger(MyErasmus.class.getName()).log(Level.SEVERE, null, ex);
        }
        this.session = new Session();
        this.loginSystem = new SQLLoginSystem(this.session);
        this.registrySystem = new SQLRegistrySystem();
        
    }
    
    private void initApp() {
        setLogo();
        configureDialogs();
        this.setLocationRelativeTo(null);
        this.completeProfilePanel.setVisible(false);
        this.setVisible(true);
        if(this.session.getUsername() == null){
            this.loginDialog.setVisible(true);
        }
    }

    private void setLogo() {
        ClassLoader classLoader = getClass().getClassLoader();
        Icon logo = new ImageIcon(classLoader.getResource("logo.png"));
        this.logoPNG.setIcon(logo);
    }
    
    private void configureDialogs() {
        this.loginDialog.pack();
        this.registerDialog.pack();
        this.studentDataDialog.pack();
        this.landlordDataDialog.pack();
        this.universityDataDialog.pack();
        this.loginDialog.setLocationRelativeTo(this);
        this.registerDialog.setLocationRelativeTo(this);
        this.studentDataDialog.setLocationRelativeTo(this);
        this.landlordDataDialog.setLocationRelativeTo(this);
        this.universityDataDialog.setLocationRelativeTo(this);
        addSystemCloseEvents();
    }

    
    private void addSystemCloseEvents(){
        this.loginDialog.addWindowListener(new WindowAdapter(){
            @Override
            public void windowClosing(WindowEvent e) {
                loginDialog.dispose();
                closeApp();
            }
        });
        this.registerDialog.addWindowListener(new WindowAdapter(){
            @Override
            public void windowClosing(WindowEvent e) {
                registerDialog.setVisible(false);
                loginDialog.setVisible(true);
                closeApp();
            }
        });
        this.studentDataDialog.addWindowListener(new WindowAdapter(){
            @Override
            public void windowClosing(WindowEvent e) {
                studentDataDialog.dispose();
                closeApp();
            }
        });
        this.landlordDataDialog.addWindowListener(new WindowAdapter(){
            @Override
            public void windowClosing(WindowEvent e) {
                landlordDataDialog.dispose();
                closeApp();
            }
        });
    }
    
