    private void bLoginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bLoginActionPerformed
        String username = userTextField.getText();
        String password = passwordTextField.getText();
        if(loginSystem.login(username, password)){
            this.loginDialog.setVisible(false); //faltaba esta linea
            giveWelcome();
            updateUI();
        } else {
            JOptionPane.showMessageDialog(null, "Usuario o contraseña incorrecto", "Datos Erróneos", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_bLoginActionPerformed

    private void giveWelcome() throws HeadlessException {
        String welcomeMessage = "¡Bienvendid@ de nuevo "+this.session.getUsername()+"!";
        JOptionPane.showMessageDialog(null, welcomeMessage, "Login Correcto", JOptionPane.INFORMATION_MESSAGE);
        if(this.session.getUserInstance() == null) {
            this.completeProfilePanel.setVisible(true);
        }
    }
    
    private Component[] getDataDialogs() {
        JDialog[] dataDialogs = new JDialog[4];
        dataDialogs[0] = this.studentDataDialog;
        dataDialogs[1] = this.landlordDataDialog;
        dataDialogs[2] = this.universityDataDialog;
        dataDialogs[3] = null;
        return dataDialogs;
    }
    
    private void updateUI() {
        jLabel7.setIcon(new javax.swing.ImageIcon(getClass().getResource("/profile.png")));
        FXHelper.updateUserFields(this.session, this.userDataPanel.getComponents());
        FXHelper.updateTabbedPanel(this.session, this.tabbedPanel);
    }
    
