    private void acceptStudentButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_acceptStudentButtonActionPerformed
        String[] userData = {this.session.getCurrentUserData().getId(), 
                            this.startDateField.getText(),
                            this.endDateField.getText(),
                            this.passportField.getText(),
                            (String) this.mobilityCombo.getSelectedItem()};
        SQLHelper.addStudentData(userData);
        this.session.setUserInstance(this.session.getType(), this.session.getCurrentUserData());
        this.completeProfilePanel.setVisible(false);
        this.studentDataDialog.dispose();
        this.updateUI();
    }//GEN-LAST:event_acceptStudentButtonActionPerformed

