    private RegisterData getRegitryData() {
        RegisterData registerData = new RegisterData();
        registerData.setUsername(this.userRegistryField.getText());
        registerData.setEmail(this.emailRegistryField.getText());
        registerData.setPassword(this.passwordRegistryField.getText());
        registerData.setConfirmPassword(this.confirmPasswordRegistryField.getText());
        registerData.setDestination(this.destinarionComboBox.getSelectedItem().toString());
        registerData.setType(getTypeFromCombobox());
        return registerData;
    }

    private String getTypeFromCombobox() {
        switch(this.userTypeComboBox.getSelectedItem().toString()){
            case "Estudiante":
                return "student";
            case "Arrendador":
                return "landlord";
            case "Universidad":
                return "uau";
        }
        return null;
    }
    
    private void closeApp(){
        try {
            SQLHelper.disconnect();
        } catch (SQLException ex) {
            Logger.getLogger(MyErasmus.class.getName()).log(Level.SEVERE, null, ex);
        }
        JOptionPane.showMessageDialog(this.registerDialog, "¡Hasta pronto!", "Cierre de Aplicación", JOptionPane.INFORMATION_MESSAGE);
        System.exit(0);
    }
    
    private void closeSession() {
        JOptionPane.showMessageDialog(this.registerDialog, "Sesión cerrada correctamente", "Cierre de Sesión", JOptionPane.INFORMATION_MESSAGE);
        this.loginSystem.logout();
        this.loginDialog.setVisible(true);
    }
    
